import {makeAutoObservable} from "mobx";

export default class DeviceStore {
    constructor() {
       this._types = [
           {id: 1, name: 'Корм для собак'},
           {id: 2, name: 'Ошейники'},
           {id: 3, name: 'Миски'},
       ]
        this._brands = [
            {id: 1, name: 'Proplan'},
            {id: 2, name: 'Wiskas'},
            {id: 3, name: 'Kitty Kat'},
        ]
        this._devices = [
            {id: 1, name: 'Ошейник 1', price: 2500, rating: 5},
            {id: 2, name: 'Ошейник 2', price: 2500, rating: 5},
            {id: 3, name: 'Ошейник 3', price: 2500, rating: 5},

        ]
        makeAutoObservable(this)
    }

    setIsAuth(bool) {
        this._isAuth = bool
    }
    setUser(user) {
        this._user = user
    }

    get isAuth() {
        return this._isAuth
    }
    get user() {
        return this._user
    }
}
